})
export class Choicebox {
  title = input<string>('');
  description = input<string | undefined>(undefined);
  value = input<string>('');
  name = input<string>('');
  disabled = input<boolean>(false);
  type = input<'radio' | 'checkbox'>('radio');
  checked = input<boolean>(false);
  onChange = output<void>();

  handleChange(): void {
    if (!this.disabled()) {
      this.onChange.emit();
    }
  }

